import { StyleSheet } from "@react-pdf/renderer"
import reactDom from "react-dom"

import global from "./global"

export default StyleSheet.create({
  ...global,

  container: {
    display: "flex",
    margin: "10 auto",
    padding: "5 10",
    width: "95%",
    height: "auto",
  },

  company: {
    textDecoration: "none",
    color: "#3489e3",
    marginBottom: 8,
  },

  header: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
  },

  job: {
    marginBottom: 8,
  },
  years: {
    textTransform: "uppercase",
  },

  list: {
    fontSize: 5,
  },
})
