import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default StyleSheet.create({
  ...global,

  container: {
    fontSize: 9,
    display: "flex",
    width: "90%",
    height: "auto",
  },

  source: {
    textDecoration: "none",
    color: "#3489e3",
    lineHeight: 0.6,
  },

  items: {
    // lineHeight: 0.5,
  },
})
