import React from "react"
import { Form, FormGroup } from "react-bootstrap"
import InputColor from "../input/Color"

const FormColors = ({ name }) => {
  const colors = [
    {
      name: "primary",
      default: "#42413e",
      display: "primary",
    },
    { name: "secondary", defaultValue: "#c4ad93", display: "secondary" },
    { name: "ternary", defaultValue: "#fafafa", display: "ternary" },
    { name: "textLight", defaultValue: "#fff", display: "light text" },
    { name: "textDark", defaultValue: "#222", display: "dark text" },
    { name: "link", defaultValue: "#54b2ff", display: "link" },
  ]
  return (
    <Form>
      {/* <FormGroup> */}
      {colors.map((color, idx) => (
        <InputColor key={idx} name={color.name} defaultValue={color.default} />
      ))}
      {/* </FormGroup> */}
    </Form>
  )
}

export default FormColors
