import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default StyleSheet.create({
  ...global,

  container: {
    width: "50%",
    height: "100%",
    position: "absolute",
    right: "49%",
    display: "flex",
    border: "1px solid black",
    flexDirection: "column",
    // marginRight: "50%",
  },

  box: {
    display: "flex",
    border: "1px solid black",
    width: "90%",
    height: "auto",
    margin: "0 auto",
  },
})
