import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default StyleSheet.create({
  ...global,
  container: {
    display: "flex",
    margin: "0 auto",
    padding: "5 10",
    width: "90%",
    height: "auto",
  },

  heading: {
    letterSpacing: 1.5,
    fontSize: 12,
    textTransform: "uppercase",
    marginBottom: 5,
    color: "#d4b88a",
  },
  technologies: {
    display: "grid",
    gridTemplateColumn: "repeat(4, 1fr)",
    // flexDirection: "column",
    // flexWrap: "wrap",
    // maxHeight: 60,
  },
})
