import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default StyleSheet.create({
  ...global,
  container: {
    textAlign: "center",
    flexDirection: "column",
    margin: "20 auto",
    width: "90%",
    height: "auto",
  },

  image: {
    maxWidth: "100%",
    height: "auto",
    margin: "0 auto",
  },

  name: {
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "center",
    maxWidth: "90%",
    fontSize: 25,
    letterSpacing: 3,
    lineHeight: 1.2,

    padding: "10 0",
    textTransform: "uppercase",
  },

  title: {
    fontSize: 18,
    marginBottom: 8,
  },
})
