import React from "react"
import { Form } from "react-bootstrap"
import InputColor from "../input/Color"
// import { colors } from "../../../lib/colors"
import { useState } from "react"

const FormColors = () => {
  const [colorPrimary, setColorPrimary] = useState()
  const [colorSecondary, setColorSecondary] = useState()
  const [colorTernary, setColorTernary] = useState()
  const [colorTextLight, setColorTextLight] = useState()
  const [colorTextDark, setColorTextDark] = useState()
  const [colorLink, setColorLink] = useState()
  const [theme, setTheme] = useState({})

  const onChangeColorPrimary = event => {
    setColorPrimary(event.target.value)
    console.log("colorPrimary", colorPrimary)
  }
  const onChangeColorSecondary = event => {
    setColorSecondary(event.target.value)
  }

  return (
    <Form>
      {/* {colors.map((color, idx) => ( */}
      <InputColor
        name="primary"
        defaultValue="#42413e"
        label="primary"
        value={colorPrimary}
        onChange={onChangeColorPrimary}
      />
      <InputColor
        name="secondary"
        defaultValue="#c4ad93"
        label="secondary"
        value={colorSecondary}
        onChange={onChangeColorSecondary}
      />
      <InputColor
        name="ternary"
        defaultValue="#fafafa"
        label="ternary"
        value={colorTernary}
        // onChange={onChange}
      />
      <InputColor
        name="textLight"
        defaultValue="#ffffff"
        label="text light"
        value={colorTextLight}
        // onChange={onChange}
      />
      <InputColor
        name="textDark"
        defaultValue="#222222"
        label="text dark"
        value={colorTextDark}
        // onChange={onChange}
      />
      <InputColor
        name="link"
        defaultValue="#54b2ff"
        label="link"
        value={colorLink}
        // onChange={onChange}
      />
      {/* ))} */}
    </Form>
  )
}

export default FormColors
