import React from "react"
import ReactDOM from "react-dom"
import { BlobProvider } from "@react-pdf/renderer"
import PSPDFKit from "./components/pdf/PSPDFKit"
import BasicPDF from "./components/pdf/Basic"
import data from "./data/data.js"
import ControlPanel from "../src/components/pdf/ControlPanel"
// import { theme } from "./styles/pdf/layout-one/themes"
import ThemeContext, { theme } from "../src/hooks/useTheme"

ReactDOM.render(
  <div>
    <ControlPanel />
    <ThemeContext.Provider value={theme}>
      <BlobProvider document={<BasicPDF data={data} />}>
        {({ blob, url, loading, error }) => {
          if (blob) {
            return <PSPDFKit blob={blob} />
          }

          if (error) {
            return error
          }

          return <div>The PDF is rendering...</div>
        }}
      </BlobProvider>
    </ThemeContext.Provider>
  </div>,
  document.getElementById("root"),
)
