import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default theme =>
  StyleSheet.create({
    ...global,

    container: {
      display: "flex",
      padding: "5 5 0",
      // margin: "0 auto",
      width: "90%",
      // maxHeight: "100%",
      border: "1px solid black",
      // display: "flex",
      // // margin: "10 0",
      // // padding: "0 10",
      // width: "100%",
      // height: "auto",
    },

    heading: {
      letterSpacing: 1.5,
      fontSize: 11,
      textTransform: "uppercase",
      color: theme.secondary,
    },

    header: {
      alignItems: "center",
      display: "flex",
      flexDirection: "row",
      justifyContent: "space-between",
    },

    school: {
      textDecoration: "none",
      color: theme.secondary,
      marginBottom: 5,
      marginTop: 8,
      textTransform: "uppercase",
    },

    description: {
      lineHeight: 1.3,
    },
  })
