import React, { Component } from "react"
// import Outer from "src/styles/pdf/Outer"
import { View, Text } from "@react-pdf/renderer"
// import Outer from "./Outer.jsx"

export default class PSPDFKit extends Component {
  containerRef = React.createRef()

  componentDidMount() {
    const url = URL.createObjectURL(this.props.blob)
    window.PSPDFKit.load({
      document: url,
      container: this.containerRef.current,
    })
  }

  componentWillUnmount() {
    window.PSPDFKit.unload(this.containerRef.current)
  }

  render() {
    return (
      <View>
        {/* <Outer /> */}
        <View
          ref={this.containerRef}
          style={{
            width: "50%",
            height: "100%",
            // position: "absolute",
            left: "50%",
            display: "flex",
          }}
        />
      </View>
    )
  }
}
