import { Text, View } from "@react-pdf/renderer"
import React from "react"
import styles from "../../styles/pdf/Education"

const Education = ({ data }) => {
  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Education</Text>
      <View>
        {data.map((school, idx) => (
          <View key={idx}>
            <View style={styles.header}>
              <Link style={styles.company} href={school.link}>
                {school.company}
              </Link>
              <Text style={styles.years}>{`${formatDate(
                school.years.start,
              )} - ${
                school.years.end === "now"
                  ? "Now"
                  : formatDate(school.years.end)
              }`}</Text>
            </View>
            <List>
              {school.tasks.map((task, idx) => (
                <Item style={styles.list} key={idx}>
                  {task}
                </Item>
              ))}
            </List>
          </View>
        ))}
      </View>
    </View>
  )
}

export default Education
