import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default theme =>
  StyleSheet.create({
    ...global,
    page: {
      display: "flex",
      padding: 2,
    },

    panel: {
      width: "2%",
      backgroundColor: theme.ternary,
      opacity: 0.5,
    },
    main: {
      fontSize: 9,
      flexDirection: "row",
      marigin: "0 auto",
      width: "100%",
      height: "80%",
      alignContent: "flex-end",
    },
  })
