import { StyleSheet, Font } from "@react-pdf/renderer"

import global from "./global"
import Prompt from "../../Prompt.ttf"

Font.register({
  family: "Prompt",
  src: Prompt,
})

export default StyleSheet.create({
  ...global,

  container: {
    fontFamily: "Prompt",
    fontSize: 10,
    display: "flex",
    padding: "3 0",
    margin: "20 auto 0",
    width: "100%",
    height: "auto",
    lineHeight: 1.2,
  },
})
