import React from "react"
import { Form, FormGroup } from "react-bootstrap"
import InputColor from "../input/Color"

const FormColors = ({ name }) => {
  const colors = [
    {
      name: "primary",
      default: "#42413e",
      display: "primary",
    },
    { name: "secondary", default: "#c4ad93", display: "secondary" },
    { name: "ternary", default: "#fafafa", display: "ternary" },
    { name: "textLight", default: "#fff", display: "light text" },
    { name: "textDark", default: "#222", display: "dark text" },
    { name: "link", default: "#54b2ff", display: "link" },
  ]
  return (
    <Form>
      {colors.map((color, idx) => (
        <InputColor
          key={idx}
          name={color.name}
          defaultValue={color.default}
          className="mb-3"
        />
      ))}
    </Form>
  )
}

export default FormColors
