import React from "react"
import { PDFDownloadLink } from "@react-pdf/renderer"
import ColsOneToOne from "./ColsOneToOne"
import styles from "../../styles/pdf/Download"
import usePdfTheme from "../../hooks/usePdfTheme"
import { PdfThemeContext } from "../../hooks/usePdfTheme"

const Download = ({ data, theme }) => {
  return (
    <PDFDownloadLink
      document={
        <PdfThemeContext.Provider value={theme}>
          <ColsOneToOne data={data} />
        </PdfThemeContext.Provider>
      }
      fileName="resume.pdf"
    >
      {({ blob, url, loading, error }) =>
        loading ? (
          "Loading document..."
        ) : (
          <button style={styles.button}>Download</button>
        )
      }
    </PDFDownloadLink>
  )
}

export default Download
