import { StyleSheet, Font } from "@react-pdf/renderer"
import global from "./global"
import RobotoCon from "../../RobotoCon.ttf"

Font.register({
  family: "RobotoCon",
  src: RobotoCon,
})
export default theme =>
  StyleSheet.create({
    ...global,
    container: {
      fontSize: 9,
      width: "100%",
      margin: "0 auto",
      display: "flex",
      flexDirection: "row",
      alignItems: "center",
      height: "100%",
      border: "1px solid white",
    },

    image: {
      width: "100%",
      height: "auto",
      margin: "10 auto",
      ...(theme.img === "circle" && { borderRadius: "50%" }),
      ...(theme.img === "square" && { borderRadius: "2%" }),
    },

    imgageContainer: {
      maxWidth: "30%",
      margin: "auto 20",
      // border: "1px solid white",
    },

    personal: {
      // margin: "0 auto",
      marginRight: 10,
      width: "90%",

      border: "1px solid white",
    },

    name: {
      fontFamily: "RobotoCon",
      color: theme.heading,
      fontSize: 28,
      letterSpacing: 4,
      lineHeight: 1.4,
      textTransform: "uppercase",
      border: "1px solid white",
    },

    title: {
      fontFamily: "RobotoCon",
      display: "flex",
      textTransform: "uppercase",
      fontSize: 10,
      letterSpacing: 2.5,
      border: "1px solid white",
    },
  })
