import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default StyleSheet.create({
  ...global,
  page: {
    display: "flex",
    flexDirection: "row-reverse",
    padding: 5,
  },

  section: {
    margin: 10,
    padding: 10,
    flexGrow: 1,
  },

  sideBar: {
    display: "flex",
    borderRight: "3px solid #c2c2d6",
    backgroundColor: "#e0e0eb",
    width: "35%",
  },

  mainBox: {
    display: "flex",
    backgroundColor: "#e6f2ff",
    width: "65%",
  },

  personal: {
    display: "flex",
    flexDirection: "column",
    margin: "20 auto",
    width: "90%",
    height: "40%",
    backgroundColor: "#e6f2ff",
  },

  image: {
    maxWidth: "90%",
    height: "auto",
    margin: "0 auto",
    backgroundColor: "white",
  },

  name: {
    fontSize: "20px",
    textAlign: "center",
    marginTop: "5rem",
  },
})
