import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default StyleSheet.create({
  ...global,

  container: {
    margin: "20 auto",
    padding: "0 10",
    display: "flex",
    width: "90%",
    height: "auto",
  },

  source: {
    textDecoration: "none",
    color: "#63a2ff",
    // color: "#3489e3",
    lineHeight: 1.5,
  },
  heading: {
    letterSpacing: 1.5,
    fontSize: 12,
    textTransform: "uppercase",
    marginBottom: 5,
    color: "#d4b88a",
  },
})
