import React from "react"
import { Form } from "react-bootstrap"
import InputColor from "../input/Color"
import { colors } from "../../../lib/colors"
import { useState } from "react"

const FormColors = () => {
  const [color, setColor] = useState()

  const onChange = event => {
    setColor(event.target.value)
    console.log("color:", color)
  }

  const [theme, setTheme] = useState({})

  return (
    <Form>
      {/* {colors.map((color, idx) => ( */}
      <InputColor
        name="primary"
        defaultValue="#42413e"
        label="primary"
        value={color}
        onChange={onChange}
      />
      <InputColor
        name="secondary"
        defaultValue="#c4ad93"
        label="secondary"
        value={color}
        onChange={onChange}
      />
      <InputColor
        name="ternary"
        defaultValue="#fafafa"
        label="ternary"
        value={color}
        onChange={onChange}
      />
      <InputColor
        name="textLight"
        defaultValue="#ffffff"
        label="text light"
        value={color}
        onChange={onChange}
      />
      <InputColor
        name="textDark"
        defaultValue="#222222"
        label="text dark"
        value={color}
        onChange={onChange}
      />
      <InputColor
        name="link"
        defaultValue="#54b2ff"
        label="link"
        value={color}
        onChange={onChange}
      />
      {/* ))} */}
    </Form>
  )
}

export default FormColors
