import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default StyleSheet.create({
  ...global,
  container: {
    width: "100%",
    display: "flex",
  },

  contact: {
    display: "flex",
    flexDirection: "row",
    width: "50%",
    lineHeight: 1.5,
    border: "1px solid white",

    // height: "20%",
  },

  email: {
    color: "white",
    textDecoration: "none",
    lineHeight: 1.3,
    textTransform: "uppercase",
    letterSpacing: 1,
  },

  heading: {
    letterSpacing: 1.5,
    fontSize: 11,
    textTransform: "uppercase",
    margin: "15 0 10",
    color: "#d4b88a",
  },
  address: {
    width: "50%",
    border: "1px solid white",
  },
})
