import { StyleSheet, Font } from "@react-pdf/renderer"
import global from "./global"
import Abel from "../../Abel.ttf"

Font.register({
  family: "Abel",
  src: Abel,
})
export default StyleSheet.create({
  ...global,

  container: {
    fontFamily: "Abel",
    display: "flex",
    backgroundColor: "#42413e",
    color: "white",
    width: "35%",
  },

  layout: {
    display: "flex",
    width: "95%",
    margin: "0 auto",
  },
})
