import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default StyleSheet.create({
  ...global,

  container: {
    display: "flex",
    margin: "10 auto",
    width: "90%",
    lineHeight: 1.2,
  },
  heading: {
    widht: "100%",
    letterSpacing: 1.5,
    fontSize: 11,
    textTransform: "uppercase",
    color: theme.secondary,
    marginTop: 5,
    marginBottom: 5,
  },
})
