import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default StyleSheet.create({
  ...global,
  container: {
    fontSize: 13,
    display: "flex",
    flexDirection: "column",
    margin: "20 auto",
    width: "85%",
    height: "40%",
    backgroundColor: "#e6f2ff",
  },

  image: {
    maxWidth: "100%",
    height: "auto",
    margin: "0 auto",
    backgroundColor: "white",
  },

  name: {
    inlineSize: "150px",
    // overflowWrap: "break-word",
    // display: "flex",
    // flexWrap: "wrap",
    // flexDirection: "column",
    maxWidth: "100%",
    fontSize: 22,
    textAlign: "center",
    padding: "10 0",
    display: "inline-block",
    height: "auto",
    overflowWrap: "break-word",
  },
  text: {},
})
