import { View, Text, Image } from "@react-pdf/renderer"
import React from "react"
import createStyles from "../../styles/pdf/layout-one/Personal"
import usePdfTheme from "../../hooks/usePdfTheme"

const Personal = ({ data }) => {
  const { firstName, lastName, photo, title } = data
  const theme = usePdfTheme()

  const styles = createStyles(theme)
  return (
    <View style={styles.container}>
      <View style={styles.image}>
        {/* for Image see: https://react-pdf.org/components#source-object */}
        <Image
          src={{
            uri: "http://localhost:3000/img/wp.jpg",
            method: "GET",
            headers: {},
            body: "",
          }}
        />
      </View>
      <View style={styles.name}>
        <Text>{firstName} </Text>
        <Text>{lastName}</Text>
      </View>
      <View>{title ? <Text style={styles.title}>{title}</Text> : null}</View>
    </View>
  )
}

export default Personal
