import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default StyleSheet.create({
  ...global,
  container: {
    fontSize: 13,
    textAlign: "center",
    flexDirection: "column",
    margin: "20 auto",
    width: "90%",
    height: "auto",
    // backgroundColor: "#e6f2ff",
    // border: "2px solid gray",
  },

  image: {
    maxWidth: "100%",
    height: "auto",
    margin: "0 auto",
    backgroundColor: "gray",
  },

  name: {
    flexDirection: "row",
    flexWrap: "wrap",
    // justifyContent: "center",
    padding: "0 10",
    maxWidth: "95%",
    fontSize: 25,
    fontWeight: "700",
    lineHeight: 1.2,

    padding: "10 0",
    textTransform: "uppercase",
  },

  title: {
    fontSize: 18,
    marginBottom: 8,
  },
})
