import { StyleSheet } from "@react-pdf/renderer"

import global from "./global"

export default StyleSheet.create({
  ...global,
  container: {
    padding: "0 10",
    display: "flex",
    // fontSize: 13,
    width: "90%",
    margin: "0 auto",
    height: "auto",
  },

  contact: {
    color: "#fff2e6",
    textDecoration: "none",
    lineHeight: 1.3,
    textTransform: "uppercase",
  },

  address: {
    display: "flex",
    flexDirection: "row",
    flexWrap: "wrap",
    paddingTop: 10,
    lineHeight: 1.3,
  },

  heading: {
    fontSize: 12,
    textTransform: "uppercase",
    marginBottom: 5,
    color: "#d4b88a",
  },
})
