import { createContext, useContext } from "react"
// import { theme } from "../styles/pdf/layout-one/themes"

import React from "react"

export const theme = {
  primary: "#42413e",
  secondary: "#fafafa",
  ternary: "#734448",
  textSide: "#fff",
  textMain: "#222",
  link: "#54b2ff",
  heading: "#d4b88a",
}

const ThemeContext = React.createContext(theme)
export default ThemeContext

