import { View, Text, Link } from "@react-pdf/renderer"
import React from "react"
import createStyles from "../../styles/pdf/Experience"
import List, { Item } from "./List"
import { format } from "date-fns"
import usePdfTheme from "../../hooks/usePdfTheme"

const Experience = ({ data }) => {
  console.log("data:", data)

  data.sort((a, b) =>
    a.positions.years.start < b.positions.years.start ? 1 : -1,
  )
  const formatDate = date => format(new Date(date), ["MMM yyyy"])
  const theme = usePdfTheme()
  const styles = createStyles(theme)

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Experience</Text>
      <View>
        {data.map((job, idx) => (
          <View key={idx}>
            <Link style={styles.company} href={job.link}>
              {job.company}
            </Link>
            {job.positions.map((position, idx) => (
              <View key={idx}>
                <View style={styles.header}>
                  <Text style={styles.title}>{position.title}</Text>
                  <Text style={styles.years}>{`${formatDate(
                    position.years.start,
                  )} - ${
                    position.years.end === "now"
                      ? "Now"
                      : formatDate(position.years.end)
                  }`}</Text>
                </View>
                <List>
                  {position.tasks.map((task, idx) => (
                    <Item key={idx}>{task}</Item>
                  ))}
                </List>
              </View>
            ))}
          </View>
        ))}
      </View>
      {/* <View>
        {data.map((job, idx) => (
          <View key={idx}>
            <View style={styles.header}>
              <Link style={styles.company} href={job.link}>
                {job.company}
              </Link>
              <Text style={styles.years}>{`${formatDate(job.years.start)} - ${
                job.years.end === "now" ? "Now" : formatDate(job.years.end)
              }`}</Text>
            </View>
            <List>
              {job.tasks.map((task, idx) => (
                <Item style={styles.list} key={idx}>
                  {task}
                </Item>
              ))}
            </List>
          </View>
        ))}
      </View> */}
    </View>
  )
}

export default Experience
